import React from "react";

const MovieCard = ({ movie }) => { // distrup the props, passing something inside the props
    return (
        <div className="movie">
            <div>
                <p>{movie.Year}</p>
            </div>
            <div>
                <img
                    src={movie.Poster !== 'N/A' ? movie.Poster : 'http://via.placeholder.com/400'} // alternative to present an error image if image is not present
                    alt={movie.Title} />
            </div>
            <div>
                <span>{movie.Type}</span>
                <h3>{movie.Title}</h3>
            </div>
        </div>
    );
}

export default MovieCard;
